float lensShapeRadius(const unsigned lensShape, const unsigned lensIndex, const float angle, const float bias) {

    GLKVector2 *shape = (lensIndex) ? leftShapes[lensShape] : rightShapes[lensShape];

    float       turns = (angle  + bias) / (2 * M_PI);
    int         index = (((int)(roundf(turns * SHAPE_ARRAY_SIZE))) + (SHAPE_ARRAY_SIZE / 4))  % SHAPE_ARRAY_SIZE;
    GLKVector2  polar = shape[index];

    return polar.x;
}